import React from "react";
import { useState } from "react";

const TableRow = (props) => {
  console.log(JSON.parse(localStorage.getItem("_risks")));

  return (
    <tr>
      <th></th>
      <td></td>
      <td></td>
      <td></td>
      <td></td>
      <td></td>
    </tr>
  );
};
export const Actions = (props) => {
  const [action_name, setAction_name] = useState("");
  const [action_desc, setAction_desc] = useState("");
  const [action_start_date, setAction_start_date] = useState("");
  const [action_end_date, setAction_end_date] = useState("");
  const [action_budget, setAction_budget] = useState("");
  const [action_severity, setAction_severity] = useState("");
  const [actions, setActions] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // console.log(
    //   project_name,
    //   project_dAction
    //   project_start_date,
    //   project_end_date,
    //   project_budget
    // );
    actions.push({
      action_name: action_name,
      action_desc: action_desc,
      action_start_date: action_start_date,
      action_end_date: action_end_date,
      action_budget: action_budget,
      action_severity: action_severity,
    });
    setActions(actions);
    const risk = props.name + "_risks";
    localStorage.setItem(risk, JSON.stringify(actions));
    // localStorage.removeItem(props.name + "_risks");
  };
  console.log(props.name);
  return (
    <div>
      {/* Actions Table  */}
      <div>
        <h1 className="title">Actions</h1>
        <table className="table">
          <thead>
            <tr>
              <th>
                <abbr>Name</abbr>
              </th>
              <th>Description</th>
              <th>
                <abbr>Severity</abbr>
              </th>
              <th>
                <abbr>Cost</abbr>
              </th>
              <th>
                <abbr>Start Date</abbr>
              </th>
              <th>
                <abbr>End Date</abbr>
              </th>
            </tr>
          </thead>
          <tbody>
            <TableRow />
            {/* <tr>
                <th>1</th>
                <td>1</td>
                <td>1</td>
                <td>1</td>
                <td>1</td>
                <td>1</td>
              </tr>
              <tr>
                <th>2</th>
                <td>Arsenal</td>
                <td>38</td>
                <td>20</td>
                <td>11</td>
                <td>7</td>
              </tr>
              <tr>
                <th>3</th>
                <td>Tottenham Hotspur</td>
                <td>38</td>
                <td>19</td>
                <td>13</td>
                <td>6</td>
              </tr> */}
          </tbody>
        </table>
      </div>
      {/* Add Action */}
      <div className="column is-half">
        <h1 className="title">Add Action</h1>
        <form onSubmit={handleSubmit}>
          {/* Action name  */}
          <div className="field">
            <label className="label">Name</label>
            <div className="control">
              <input
                className="input is-success"
                type="text"
                value={action_name}
                onChange={(ev) => setAction_name(ev.target.value)}
                placeholder="Text input"
              />
            </div>
          </div>
          {/* Action Description  */}
          <div className="field">
            <label className="label"> Description</label>
            <div className="control">
              <textarea
                className="textarea"
                value={action_desc}
                onChange={(ev) => setAction_desc(ev.target.value)}
                placeholder="Textarea"
              ></textarea>
            </div>
          </div>
          {/* Action Severity  */}
          <div className="field">
            <label className="label">Severity</label>
            <div className="control">
              <div className="select">
                <select
                  type="text"
                  value={action_severity}
                  onChange={(ev) => setAction_severity(ev.target.value)}
                >
                  <option value="High">High</option>
                  <option value="Moderate">Moderate</option>
                  <option value="Low">Low</option>
                </select>
              </div>
            </div>

            {/* Action Budget  */}

            <div>
              <label className="label">Cost</label>
              <p className="control">
                <span className="select">
                  <select>
                    <option>$</option>
                    <option>£</option>
                    <option>€</option>
                  </select>
                </span>
              </p>
              <p className="control">
                <input
                  className="input"
                  type="text"
                  value={action_budget}
                  onChange={(ev) => setAction_budget(ev.target.value)}
                  placeholder="Amount of money (0.00)"
                />
              </p>
            </div>
          </div>
          {/* Action Start Date */}

          <div className="field">
            <label className="label">Start Date</label>
            <div className="control">
              <input
                className="input is-success"
                type="date"
                value={action_start_date}
                onChange={(ev) => setAction_start_date(ev.target.value)}
              />
            </div>
          </div>
          {/* Action End Date  */}

          <div className="field">
            <label className="label">End Date</label>
            <div className="control">
              <input
                className="input is-success"
                type="date"
                value={action_end_date}
                onChange={(ev) => setAction_end_date(ev.target.value)}
              />
            </div>
          </div>

          {/* Submit Action  */}
          <div className="buttons">
            <button className="button is-primary" type="submit">
              Add New Action
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
